import axios from 'axios';
import * as i0 from "@angular/core";
export class AddressService {
    async getAddresses(customerId) {
        const res = await axios.get(`http://localhost:4000/api/customers/${customerId}/addresses`);
        return res.data;
    }
    async createAddress(customerId, data) {
        const res = await axios.post(`http://localhost:4000/api/customers/${customerId}/addresses`, data);
        return res.data;
    }
    async updateAddress(customerId, addressId, data) {
        const res = await axios.put(`http://localhost:4000/api/customers/${customerId}/addresses/${addressId}`, data);
        return res.data;
    }
    async deleteAddress(customerId, addressId) {
        const res = await axios.delete(`http://localhost:4000/api/customers/${customerId}/addresses/${addressId}`);
        return res.data;
    }
}
